<?php

use App\Services\ZenAiService;
use Illuminate\Support\Facades\Log;

require __DIR__.'/vendor/autoload.php';

$app = require_once __DIR__.'/bootstrap/app.php';
$kernel = $app->make(Illuminate\Contracts\Console\Kernel::class);
$kernel->bootstrap();

$service = new ZenAiService();

// Check if key is loaded
$reflection = new ReflectionClass($service);
$property = $reflection->getProperty('geminiKey');
$property->setAccessible(true);
$key = $property->getValue($service);

echo "Gemini Key Status: " . (empty($key) ? "MISSING" : "PRESENT (Length: " . strlen($key) . ")") . "\n";
echo "Config Key: " . config('services.gemini.key') . "\n";
echo "Env Key: " . env('GEMINI_API_KEY') . "\n";

// Create a dummy VALID JPEG file to test
$dummyPath = 'scans/test.jpg';
if (!file_exists(storage_path('app/public/scans'))) {
    mkdir(storage_path('app/public/scans'), 0755, true);
}
// 1x1 White JPEG Base64
$jpgData = base64_decode('');
file_put_contents(storage_path('app/public/' . $dummyPath), $jpgData);

echo "Testing scanMedia with Gemini...\n";
$result = $service->scanMedia($dummyPath);
print_r($result);
